import React, { useState } from 'react'
import { View, Text, StyleSheet } from 'react-native'

// Import Configs
import colors from '../../config/colors'

// Import Libraries
import * as yup from 'yup'
import { Picker } from '@react-native-picker/picker'

// Import Components
import { AppForm, AppFormField, SubmitButton } from '../../components/forms'

// Validation Schema
const validationSchema = yup.object().shape({
  title: yup.string().required().label('Job Title'),
  skills: yup.string().required().label('Skills'),
})

const AddJobScreen = ({ navigation }) => {
  const [jobAvailability, setAvailability] = useState(undefined)
  const [jobType, setType] = useState()

  return (
    <View style={styles.container}>
      <AppForm
        initialValues={{
          title: '',
          description: '',
          availability: jobAvailability,
          skills: '',
          type: '',
          expected_salary: '',
        }}
        onSubmit={(values) => {
          console.log(values.availability)
        }}
        validationSchema={validationSchema}
      >
        <View style={styles.form}>
          <View style={styles.formGroup}>
            <Text>Job Title</Text>
            <AppFormField placeholder='Title' name='title' />
          </View>

          <View style={styles.formGroup}>
            <Text>Description</Text>
            <AppFormField
              placeholder='Description'
              name='description'
              multiline={true}
              numberOfLines={5}
              style={{
                minHeight: 200,
                textAlignVertical: 'top',
                justifyContent: 'flex-start',
              }}
            />
          </View>

          <View style={styles.formGroup}>
            <Text>Type</Text>
            <Picker
              selectedValue={'dsa'}
              onValueChange={(itemValue) => setType(itemValue)}
              style={styles.dropdown}
              name='type'
            >
              <Picker.Item label='Select Job Type' />
              <Picker.Item label='Part Time' value='Part Time' />
              <Picker.Item label='Fulltime' value='Fulltime' />
            </Picker>
          </View>

          <View style={styles.formGroup}>
            <Text>Availability</Text>
            <Picker
              selectedValue={jobAvailability}
              onValueChange={(itemValue) => setAvailability(itemValue)}
              name='availability'
              style={styles.dropdown}
            >
              <Picker.Item label='Select Job Availability' />
              <Picker.Item label='in 1 week' value='in 1 week' />
              <Picker.Item label='in 2 weeks' value='in 2 weeks' />
              <Picker.Item label='in a month' value='in a month' />
              <Picker.Item
                label='more than a month'
                value='more than a month'
              />
            </Picker>
          </View>

          <View style={styles.formGroup}>
            <Text>Skills</Text>
            <AppFormField placeholder='Skills' name='skills' />
            <Text style={styles.helpText}>
              Please add comma (, ) separated skills
            </Text>
          </View>

          <View style={styles.formGroup}>
            <Text>Expected Salary</Text>
            <AppFormField
              placeholder='Expected Salary'
              name='expected_salary'
            />
          </View>

          <SubmitButton title='Add Job' />
        </View>
      </AppForm>
      <Text
        style={styles.authLinkText}
        onPress={() => navigation.navigate('Login')}
      >
        Already have an account? Login Here
      </Text>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: colors.white,
    display: 'flex',
    flex: 1,
  },
  heading: {
    fontSize: 24,
    color: colors.white,
    fontWeight: 'bold',
  },
  form: {
    width: '100%',
    marginVertical: 8,
    paddingRight: 25,
    paddingLeft: 25,
  },
  authLinkText: {
    marginTop: 5,
    color: colors.white,
    textAlign: 'left',
    width: '84%',
  },
  helpText: {
    fontSize: 12,
    fontStyle: 'italic',
    color: 'gray',
  },
  formGroup: {
    marginBottom: '0.5rem',
  },
  form: {
    padding: '1rem',
  },
  dropdown: {
    width: '100%',
    padding: 8,
    paddingLeft: 12,
    borderColor: 'gray',
    borderWidth: 1,
    borderRadius: 5,
    flexDirection: 'row',
    borderColor: colors.medium,
    borderWidth: 1,
    alignItems: 'center',
    marginVertical: 5,
    backgroundColor: colors.white,
    minHeight: 47,
  },
})

export default AddJobScreen
