import React, { useState } from 'react'
import { View, Text, StyleSheet } from 'react-native'

// Import Configs
import colors from '../../config/colors'

// Import Libraries
import * as yup from 'yup'
import { Picker } from '@react-native-picker/picker'

// Import Components
import { AppForm, AppFormField, SubmitButton } from '../../components/forms'

// Validation Schema
const validationSchema = yup.object().shape({
  title: yup.string().required().label('Job Title'),
  availability: yup.string().required().label('availability'),
  skills: yup.string().required().label('Skills'),
})

const AddJobScreen = ({ navigation }) => {
  const [selectedLanguage, setSelectedLanguage] = useState()

  return (
    <View style={styles.container}>
      <AppForm
        initialValues={{
          title: '',
          description: '',
          availability: '',
          skills: selectedLanguage,
          type: '',
          expected_salary: '',
        }}
        onSubmit={(values) => {
          console.log(values)
        }}
        validationSchema={validationSchema}
      >
        <View style={styles.form}>
          <View style={styles.formGroup}>
            <Text>Job Title</Text>
            <AppFormField placeholder='Title' name='title' />
          </View>
          <View style={styles.formGroup}>
            <Text>Expected Salary</Text>
            <AppFormField
              placeholder='Expected Salary'
              name='expected_salary'
            />
          </View>
          <View style={styles.formGroup}>
            <Text>Skills</Text>
            <AppFormField placeholder='Skills' name='skills' />
          </View>
          <View style={styles.formGroup}>
            <Text>Type</Text>
            <AppFormField placeholder='Type' name='type' />
            <Text style={styles.helpText}>
              Please add comma (, ) separated skills
            </Text>
          </View>
          <View style={styles.formGroup}>
            <Text>Skills</Text>
            <AppFormField placeholder='Skills' name='availability' />
          </View>
          <View style={styles.formGroup}>
            <Text>Description</Text>
            <AppFormField
              placeholder='Description'
              name='description'
              multiline={true}
              numberOfLines={5}
              style={{
                minHeight: 200,
                textAlignVertical: 'top',
                justifyContent: 'flex-start',
              }}
            />
          </View>
          <SubmitButton title='Add Job' />
        </View>
      </AppForm>
      <Text
        style={styles.authLinkText}
        onPress={() => navigation.navigate('Login')}
      >
        Already have an account? Login Here
      </Text>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: colors.white,
    display: 'flex',
    flex: 1,
  },
  heading: {
    fontSize: 24,
    color: colors.white,
    fontWeight: 'bold',
  },
  form: {
    width: '100%',
    marginVertical: 8,
    paddingRight: 25,
    paddingLeft: 25,
  },
  authLinkText: {
    marginTop: 5,
    color: colors.white,
    textAlign: 'left',
    width: '84%',
  },
  helpText: {
    fontSize: 12,
    fontStyle: 'italic',
    color: 'gray',
  },
  formGroup: {
    marginBottom: '0.5rem',
  },
  form: {
    padding: '1rem',
  },
})

export default AddJobScreen
