import {
  REGISTRATION_SUCCESS,
  REGISTRATION_FAIL,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  GET_PROFILE_SUCCESS,
  GET_PROFILE_FAIL,
} from '../actions/types'

const initialState = {
  user: null,
  isAuthenticated: localStorage.getItem('token') ? true : false,
  token: localStorage.getItem('token'),
  profile: {},
}

const Auth = (state = initialState, action) => {
  const { payload, type } = action

  switch (type) {
    case REGISTRATION_SUCCESS:
      return {
        ...state,
      }
    case LOGIN_SUCCESS:
    case GET_PROFILE_SUCCESS:
      localStorage.setItem('token', payload.refreshToken)
      return {
        ...state,
        user: payload,
        isAuthenticated: true,
      }
    case GET_PROFILE_SUCCESS:
      return {
        ...state,
        profile: payload,
      }
    case REGISTRATION_FAIL:
    case LOGIN_FAIL:
    case GET_PROFILE_FAIL:
      return {
        ...state,
        user: null,
        isAuthenticated: false,
      }
    default:
      return state
  }
}
export default Auth
