import React, {useState} from 'react'
import { View, Text, StyleSheet, Picker } from 'react-native'

// Import Configs
import colors from '../../config/colors'

// Import Libraries
import * as yup from 'yup'

// Import Components
import { AppForm, AppFormField, SubmitButton } from '../../components/forms'

// Validation Schema
const validationSchema = yup.object().shape({
  title: yup.string().required().label('Job Title'),
  description: yup.string().required().label('description'),
  availability: yup.string().required().label('availability'),
  skills: yup.string().required().label('Skills'),
})

const AddJobScreen = ({ navigation }) => {
    const [selectedLanguage, setSelectedLanguage] = useState()

  return (
    <View style={styles.container}>
      <Text style={styles.heading}>Register Here</Text>
      <AppForm
        initialValues={{
          title: '',
          description: '',
          availability: '',
          skills: '',
          type: '',
          expected_salary: '',
        }}
        onSubmit={(values) => {
          //   signUp(
          //     values.username,
          //     values.email,
          //     values.password,
          //     values.password2
          //   )
          console.log(values)
        }}
        validationSchema={validationSchema}
      >
        <View style={styles.form}>
          <AppFormField placeholder='Title' name='title' />
          <AppFormField placeholder='Expected Salary' name='expected_salary' />
          <AppFormField placeholder='Skills' name='skills' />
          <Text style={styles.helpText}>
            Please add comma (, ) separated skills
          </Text>
          <AppFormField
            placeholder='Description'
            name='description'
            multiline={true}
            numberOfLines={5}
            style={{
              minHeight: 200,
              textAlignVertical: 'top',
              justifyContent: 'flex-start',
            }}
          />

          <SubmitButton title='Add Job' />
        </View>
      </AppForm>
      <Text
        style={styles.authLinkText}
        onPress={() => navigation.navigate('Login')}
      >
        Already have an account? Login Here
      </Text>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: colors.white,
    display: 'flex',
    flex: 1,
  },
  heading: {
    fontSize: 24,
    color: colors.white,
    fontWeight: 'bold',
  },
  form: {
    width: '100%',
    marginVertical: 8,
    paddingRight: 25,
    paddingLeft: 25,
  },
  authLinkText: {
    marginTop: 5,
    color: colors.white,
    textAlign: 'left',
    width: '84%',
  },
  helpText: {
    fontSize: 12,
    fontStyle: 'italic',
    color: 'gray',
  },
})

export default AddJobScreen
