import React, {useEffect} from 'react'
import { View, Text, StyleSheet, ScrollView } from 'react-native'

// Import Configs
import colors from '../../config/colors'

// Import Libraries
import { Cell, TableView } from 'react-native-tableview-simple'

// Import Redux Stuff
import { connect } from 'react-redux'
import { getStudentProfile } from '../../actions/students'

const JobDetailScreen = ({ getStudentProfile, student, route }) => {
  useEffect(() => {
    
  })
  return (
    <ScrollView style={styles.container}>
      <Text style={styles.title}>John Doe</Text>
      <Text style={styles.description}>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec vel risus
        malesuada, auctor leo vitae, blandit ante. Nulla non ligula erat. Donec
        tincidunt orci quis aliquam mollis. Sed tristique dignissim lacus, sit
        amet lacinia nulla cursus at. Quisque mi eros, mollis vitae porttitor
        at, placerat ac lacus. Nulla blandit ornare aliquam. Curabitur auctor
        purus hendrerit scelerisque dapibus. Sed dolor tortor, scelerisque quis
        volutpat vitae, cursus eget ex.
      </Text>

      <View style={styles.details}>
        <Text style={styles.small_heading}>Details</Text>
        <TableView>
          <Cell
            cellStyle='RightDetail'
            title='Email'
            detail='john@example.com'
            contentContainerStyle={{
              paddingLeft: 0,
              paddingRight: 0,
            }}
          />
          <Cell
            cellStyle='RightDetail'
            title='Phone'
            detail='032928125418'
            contentContainerStyle={{ paddingLeft: 0, paddingRight: 0 }}
          />
          <Cell
            cellStyle='RightDetail'
            title='Address'
            detail='Faisalabad'
            contentContainerStyle={{ paddingLeft: 0, paddingRight: 0 }}
          />
          <Cell
            cellStyle='RightDetail'
            title='Current Degree'
            detail='Engineering'
            contentContainerStyle={{ paddingLeft: 0, paddingRight: 0 }}
          />
          <Cell
            cellStyle='RightDetail'
            title='Admission Date'
            detail='22-12-2017'
            contentContainerStyle={{ paddingLeft: 0, paddingRight: 0 }}
          />
        </TableView>
      </View>

      <View style={styles.grades}>
        <Text style={styles.small_heading}>Recent Grades</Text>
        <TableView>
          <Cell
            cellStyle='RightDetail'
            title='English'
            detail='A+'
            contentContainerStyle={{
              paddingLeft: 0,
              paddingRight: 0,
            }}
          />
          <Cell
            cellStyle='RightDetail'
            title='Math'
            detail='A+'
            contentContainerStyle={{ paddingLeft: 0, paddingRight: 0 }}
          />
          <Cell
            cellStyle='RightDetail'
            title='Urdu'
            detail='A+'
            contentContainerStyle={{ paddingLeft: 0, paddingRight: 0 }}
          />
          <Cell
            cellStyle='RightDetail'
            title='Physics'
            detail='A+'
            contentContainerStyle={{ paddingLeft: 0, paddingRight: 0 }}
          />
          <Cell
            cellStyle='RightDetail'
            title='Chemistry'
            detail='A+'
            contentContainerStyle={{ paddingLeft: 0, paddingRight: 0 }}
          />
        </TableView>
      </View>
    </ScrollView>
  )
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: colors.white,
    display: 'flex',
    flex: 1,
  },
  title: {
    fontSize: 22,
    marginBottom: 10,
  },
  description: {
    fontSize: 16,
  },
  details: {
    marginTop: 20,
    marginBottom: 10,
  },
  grades: {
    marginBottom: 20,
  },
  small_heading: {
    fontSize: 17,
    fontWeight: 'bold',
  },
  skills: {
    fontStyle: 'italic',
  },
})

// Import States
const mapStateToProps = (state) => ({
  student: state.Students.student,
})

export default connect(mapStateToProps, { getStudentProfile })(JobDetailScreen)
