import { COMPLETE_S_PROFILE_SUCCESS, COMPLETE_S_PROFILE_FAIL } from './types'
import { firestore } from '../config/firebase'

/* ============================== */
/* CREATE NEW USER PROFILE */
/* ============================== */
export const completeStudentProfile = (data) => async (dispatch) => {
  console.log(firestore)
  try {
    firestore.collection('cities').doc('new-city-id').set(data)
    firestore.collection('profiles').doc(data.id).update({
      student_id: data.student_id,
      first_name: data.first_name,
      lat_name: data.lat_name,
      about: data.about,
      dob: data.dob,
      phone: data.phone,
      address: data.address,
      city: data.city,
      state: data.state,
      current_degree: data.current_degree,
      admission_date: data.admission_date,
    })
    dispatch({
      type: COMPLETE_S_PROFILE_SUCCESS,
      payload: res,
    })
  } catch (err) {
    dispatch({
      type: COMPLETE_S_PROFILE_FAIL,
    })
  }
}
