import React, { useContext } from 'react';
import { Platform, SafeAreaView, StyleSheet, View, } from 'react-native';
import { ThemeContext } from './Theme';
const Separator = ({ backgroundColor, tintColor, isHidden = false, insetLeft = 15, insetRight = 0, withSafeAreaView = Platform.OS === 'ios'
    ? parseInt(`${Platform.Version}`, 10) <= 10
        ? false
        : true
    : true, }) => {
    const theme = useContext(ThemeContext);
    const localStyles = {
        separator: [
            styles.separator,
            { backgroundColor: backgroundColor || theme.colors.background },
        ],
        separatorInner: [
            styles.separatorInner,
            {
                backgroundColor: isHidden
                    ? 'transparent'
                    : tintColor || theme.colors.separatorColor,
                marginLeft: insetLeft,
                marginRight: insetRight,
            },
        ],
    };
    if (withSafeAreaView) {
        return (<SafeAreaView style={localStyles.separator}>
        <View style={localStyles.separatorInner}/>
      </SafeAreaView>);
    }
    return (<View style={localStyles.separator}>
      <View style={localStyles.separatorInner}/>
    </View>);
};
const styles = StyleSheet.create({
    separator: {},
    separatorInner: {
        height: StyleSheet.hairlineWidth,
    },
});
export default Separator;
